import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const CreateUser = () => {
  const [newUser, setNewUser] = useState({ LOGIN_NAME: '', Email: '', LOGIN_PASSWORD: '', role_id: '', Address: '', delete: '', status: '' });
  const navigate = useNavigate();

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewUser({ ...newUser, [name]: value });
  };

  const addUser = async (e) => {
    e.preventDefault();
    try {
      await axios.post('https://localhost:7099/api/User', newUser);
      navigate('/');
    } catch (error) {
      console.error('Error adding user:', error);
    }
  };

  return (
    <div>
      <h2>Create User</h2>
      <form onSubmit={addUser}>
        <input type="text" name="LOGIN_NAME" value={newUser.LOGIN_NAME} onChange={handleInputChange} placeholder="Login Name" required />
        <input type="email" name="Email" value={newUser.Email} onChange={handleInputChange} placeholder="Email" required />
        <input type="password" name="LOGIN_PASSWORD" value={newUser.LOGIN_PASSWORD} onChange={handleInputChange} placeholder="Password" required />
        <input type="text" name="role_id" value={newUser.role_id} onChange={handleInputChange} placeholder="Role ID" required />
        <input type="text" name="Address" value={newUser.Address} onChange={handleInputChange} placeholder="Address" required />
        <input type="text" name="delete" value={newUser.delete} onChange={handleInputChange} placeholder="Delete" />
        <input type="text" name="status" value={newUser.status} onChange={handleInputChange} placeholder="Status" />
        <button type="submit">Create</button>
      </form>
    </div>
  );
};

export default CreateUser;
