import React, { useState, useEffect } from 'react';
import { NavLink } from 'react-router-dom';
import CreateUser from './CreateUser';
import axios from 'axios';

const User = () => {
  const [users, setUsers] = useState([]);
  const [editingUser, setEditingUser] = useState(null);

  useEffect(() => {
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    try {
      const response = await axios.get('https://localhost:7099/api/User');
      setUsers(response.data);
    } catch (error) {
      console.error('Error fetching users:', error);
    }
  };

  const deleteUser = async (id) => {
    try {
      await axios.delete(`https://localhost:7099/api/User/${id}`);
      fetchUsers();
    } catch (error) {
      console.error('Error deleting user:', error);
    }
  };

  const startEditing = (user) => {
    setEditingUser(user);
  };

  const cancelEditing = () => {
    setEditingUser(null);
  };

  const saveUser = async (e) => {
    e.preventDefault();
    try {
      await axios.put(`https://localhost:7099/api/User/${editingUser.LOGIN_ID}`, editingUser);
      setEditingUser(null);
      fetchUsers();
    } catch (error) {
      console.error('Error saving user:', error);
    }
  };

  const handleEditChange = (e) => {
    const { name, value } = e.target;
    setEditingUser({ ...editingUser, [name]: value });
  };

  return (
    <div>
      <h1>Users</h1>
      <button><NavLink to="/User/CreateUser">Create User</NavLink></button>
      <ul>
        {users.map(user => (
          <li key={user.LOGIN_ID}>
            Name: {user.LOGIN_NAME} - Email: ({user.Email}) - Role: {user.role_id} - Address: {user.Address} - Status: {user.status}
            <button onClick={() => startEditing(user)}>Edit</button>
            <button onClick={() => deleteUser(user.LOGIN_ID)}>Delete</button>
          </li>
        ))}
      </ul>
      {editingUser && (
        <form onSubmit={saveUser}>
          <h2>Edit User</h2>
          <input type="text" name="LOGIN_NAME" value={editingUser.LOGIN_NAME} onChange={handleEditChange} placeholder="Login Name" required />
          <input type="email" name="Email" value={editingUser.Email} onChange={handleEditChange} placeholder="Email" required />
          <input type="password" name="LOGIN_PASSWORD" value={editingUser.LOGIN_PASSWORD} onChange={handleEditChange} placeholder="Password" required />
          <input type="text" name="role_id" value={editingUser.role_id} onChange={handleEditChange} placeholder="Role ID" required />
          <input type="text" name="Address" value={editingUser.Address} onChange={handleEditChange} placeholder="Address" required />
          <input type="text" name="delete" value={editingUser.delete} onChange={handleEditChange} placeholder="Delete" />
          <input type="text" name="status" value={editingUser.status} onChange={handleEditChange} placeholder="Status" />
          <button type="submit">Save</button>
          <button onClick={cancelEditing}>Cancel</button>
        </form>
      )}
    </div>
  );
};

export default User;
