import React, { useState, useEffect } from 'react';
import axios from 'axios';

const User = () => {
  const [users, setUsers] = useState([]);
  const [newUser, setNewUser] = useState({ LOGIN_NAME: '', Email: '', LOGIN_PASSWORD: '', role_id: '', Address: '', delete: '', status: '' });
  const [editingUser, setEditingUser] = useState(null);

  useEffect(() => {
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    try {
      const response = await axios.get('https://localhost:7099/api/User');
      setUsers(response.data);
    } catch (error) {
      console.error('Error fetching users:', error);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewUser({ ...newUser, [name]: value });
  };

  const addUser = async (e) => {
    e.preventDefault();
    try {
      await axios.post('https://localhost:7099/api/User', newUser);
      setNewUser({ LOGIN_NAME: '', Email: '', LOGIN_PASSWORD: '', role_id: '', Address: '', delete: '', status: '' });
      fetchUsers();
    } catch (error) {
      console.error('Error adding user:', error);
    }
  };

  const deleteUser = async (id) => {
    try {
      await axios.delete(`https://localhost:7099/api/User/${id}`);
      fetchUsers();
    } catch (error) {
      console.error('Error deleting user:', error);
    }
  };

  const startEditing = (user) => {
    setEditingUser(user);
  };

  const cancelEditing = () => {
    setEditingUser(null);
  };

  const saveUser = async (e) => {
    e.preventDefault();
    try {
      await axios.put(`https://your-api-url/api/users/${editingUser.LOGIN_ID}`, editingUser);
      setEditingUser(null);
      fetchUsers();
    } catch (error) {
      console.error('Error saving user:', error);
    }
  };

  const handleEditChange = (e) => {
    const { name, value } = e.target;
    setEditingUser({ ...editingUser, [name]: value });
  };

  return (
    <div>
      <h1>Users</h1>
      <form onSubmit={addUser}>
        <input type="text" name="LOGIN_NAME" value={newUser.LOGIN_NAME} onChange={handleInputChange} placeholder="Login Name" required />
        <input type="email" name="Email" value={newUser.Email} onChange={handleInputChange} placeholder="Email" required />
        <input type="password" name="LOGIN_PASSWORD" value={newUser.LOGIN_PASSWORD} onChange={handleInputChange} placeholder="Password" required />
        <input type="text" name="role_id" value={newUser.role_id} onChange={handleInputChange} placeholder="Role ID" required />
        <input type="text" name="Address" value={newUser.Address} onChange={handleInputChange} placeholder="Address" required />
        <input type="text" name="delete" value={newUser.delete} onChange={handleInputChange} placeholder="Delete" />
        <input type="text" name="status" value={newUser.status} onChange={handleInputChange} placeholder="Status" />
        <button type="submit">Add User</button>
      </form>
      <hr />
      <ul>
        {users.map(user => (
          <li key={user.LOGIN_ID}>
            Name: {user.LOGIN_NAME}- ({user.Email}) - Role: {user.role_id} - Address: {user.Address} - Status: {user.status}
            <button onClick={() => startEditing(user)}>Edit</button>
            <button onClick={() => deleteUser(user.LOGIN_ID)}>Delete</button>
          </li>
        ))}
      </ul>
      {editingUser && (
        <form onSubmit={saveUser}>
          <h2>Edit User</h2>
          <input type="text" name="LOGIN_NAME" value={editingUser.LOGIN_NAME} onChange={handleEditChange} placeholder="Login Name" required />
          <input type="email" name="Email" value={editingUser.Email} onChange={handleEditChange} placeholder="Email" required />
          <input type="password" name="LOGIN_PASSWORD" value={editingUser.LOGIN_PASSWORD} onChange={handleEditChange} placeholder="Password" required />
          <input type="text" name="role_id" value={editingUser.role_id} onChange={handleEditChange} placeholder="Role ID" required />
          <input type="text" name="Address" value={editingUser.Address} onChange={handleEditChange} placeholder="Address" required />
          <input type="text" name="delete" value={editingUser.delete} onChange={handleEditChange} placeholder="Delete" />
          <input type="text" name="status" value={editingUser.status} onChange={handleEditChange} placeholder="Status" />
          <button type="submit">Save</button>
          <button onClick={cancelEditing}>Cancel</button>
        </form>
      )}
    </div>
  );
};

export default User;
