import React, { useState, useEffect } from 'react';
import { NavLink } from 'react-router-dom';
import axios from 'axios';

const User = () => {
  const [users, setUsers] = useState([]);
  const [editingUser, setEditingUser] = useState(null);

  useEffect(() => {
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    try {
      const response = await axios.get('https://localhost:7099/api/User');
      console.log('Fetched users:', response.data); // Debugging: Log fetched data
      setUsers(response.data);
    } catch (error) {
      console.error('Error fetching users:', error);
    }
  };

  const deleteUser = async (id) => {
    try {
      await axios.delete(`https://localhost:7099/api/User/${id}`);
      fetchUsers();
    } catch (error) {
      console.error('Error deleting user:', error);
    }
  };

  const startEditing = (user) => {
    setEditingUser(user);
  };

  const cancelEditing = () => {
    setEditingUser(null);
  };

  const saveUser = async (e) => {
    e.preventDefault();
    try {
      await axios.post('https://localhost:7099/api/User/update', editingUser);
      setEditingUser(null);
      fetchUsers();
    } catch (error) {
      console.error('Error saving user:', error);
    }
  };

  const handleEditChange = (e) => {
    const { name, value } = e.target;
    setEditingUser({ ...editingUser, [name]: value });
  };

  return (
    <div>
      <h1>Users</h1>
      <button>
        <NavLink to="/User/createUser">Create User</NavLink>
      </button>
      <ul>
        {users.map(user => (
          <li key={user.logiN_ID}>
            Name: {user.logiN_NAME} - Email: {user.email} - Role: {user.role_id} - Address: {user.address} - Status: {user.status}
            <button onClick={() => startEditing(user)}>Edit</button>
            <button onClick={() => deleteUser(user.logiN_ID)}>Delete</button>
          </li>
        ))}
      </ul>
      {editingUser && (
        <form onSubmit={saveUser}>
          <h2>Edit User</h2>
          <input
            type="text"
            name="logiN_NAME"
            value={editingUser.logiN_NAME}
            onChange={handleEditChange}
            placeholder="Login Name"
            required
          />
          <input
            type="email"
            name="email"
            value={editingUser.email}
            onChange={handleEditChange}
            placeholder="Email"
            required
          />
          <input
            type="text"
            name="role_id"
            value={editingUser.role_id}
            onChange={handleEditChange}
            placeholder="Role ID"
            required
          />
          <input
            type="text"
            name="address"
            value={editingUser.address}
            onChange={handleEditChange}
            placeholder="Address"
            required
          />
          <input
            type="text"
            name="status"
            value={editingUser.status}
            onChange={handleEditChange}
            placeholder="Status"
          />
          <button type="submit">Save</button>
          <button type="button" onClick={cancelEditing}>Cancel</button>
        </form>
      )}
    </div>
  );
};

export default User;
